export async function main({ update, newSprite, newSlide, sleep, colors }: Package) {
  // 72 x 35
  const main = newSlide();
  const line: Sprite[] = [];
  for (let i = 0; i < 35; i++) {
    const sprite = newSprite({
      y: i,
      texture: ["@@@@"],
      color: "rgb(0 29 8)",
    });
    line.push(sprite);
    main.addSprite(sprite);
  }

  main.addSprite(
    newSprite({
      x: 1,
      y: 1,
      texture: ["Rainbow", "By: Ashley"],
      color: "white",
      noClip: true,
    })
  );

  
  let i = 0;
  let isOn = true;
  let isForward = true;
  let amp = 25;
  let wavelength = 10;
  let textWidth = 3;

  main.addSprite(
    newSprite({
      x: 1,
      y: 22,
      texture: [
        "-= Settings =-",
        "",
        () => `Amplitude : ${amp} (+1/-2)`,
        "",
        () => `Wavelength : ${wavelength} (+3/-4)`,
        "",
        () => `Text Width : ${textWidth} (+5/-6)`,
        "",
        "Pause (p)",
        "",
        "Reverse (r)"
      ],
      color: "grey",
      noClip: true,
    })
  );

  main.addListener("1", () => {
    amp++
    sinewave()
  });
  main.addListener("2", () => {
    if (amp > 0) amp -= 1;
    sinewave()
  });
  main.addListener("3", () => {
    wavelength++
    sinewave()
  });
  main.addListener("4", () => {
    if (wavelength > 0) wavelength -= 1;
    sinewave()
  });
  main.addListener("5", () => {
    textWidth++
    sinewave()
  });
  main.addListener("6", () => {
    if (textWidth > 0) textWidth -= 1;
    sinewave()
  });


  function sinewave () {
    const color = colors[Math.abs(i) % colors.length];
      let x: number;
      line.forEach((sprite) => {
        x = Math.round(Math.sin((sprite.y + i) / wavelength) * amp);
        sprite.x = x + 60 - amp;
        sprite.color = color;
        sprite.texture = ["@".repeat(textWidth)];
      });
      
      beep(50,  x * 100 + 1000, textWidth)
      update(main);
  }

  let a=new AudioContext() // browsers limit the number of concurrent audio contexts, so you better re-use'em

  function beep(vol: number, freq: number, duration: number){
    let v=a.createOscillator()
    let u=a.createGain()
    v.connect(u)
    v.frequency.value=freq
    v.type="square"
    u.connect(a.destination)
    u.gain.value=vol*0.01
    v.start(a.currentTime)
    v.stop(a.currentTime+duration*0.001)
  }

  async function runWave() {
    while (isOn) {
      sinewave()
      isForward ? i++ : (i -= 1);
      //amp = Math.round((Math.sin(i / 100)) * 50);
      //var snd = new Audio("data:audio/wav;base64,");  
      //snd.play();
      //wavelength = Math.round((Math.tan(i / 100) - 1) * -1);
      await sleep(0);
    }
  }

  main.addListener("p", () => {
    isOn = !isOn;
    if (isOn) runWave();
  });

  main.addListener("r", () => (isForward = !isForward));

  runWave();
}
